import pandas as pdimport numpy as npfrom rdkit import Chemfrom rdkit import DataStructsimport plotly as px# Opening the reaction databasedata = pd.read_csv(r'/Users/maxbutterworth/Library/CloudStorage/OneDrive-TheUniversityofNottingham/Nottingham/Year 3/Synoptic Module/Research Project/Solvent-Selection/Database copy.csv')data = data.to_numpy() # Assigning Pandas database to numpy array# Defining category arraysreagents = data[0:, 0]solvents = data[0:, 1]smiles = data[0:, 6]ms = [] # Defining the molecular SMILES listfps = [] # Defining the fingerprints list# Setting up the SMILES fingerprintsfor i in range(0, len(smiles)):    ms.append(Chem.rdmolfiles.MolFromSmiles(smiles[i]))    fps.append(Chem.RDKFingerprint(ms[i]))  # Defining a function to search the reaction# def reaction_search(input_smiles):